export const initialState = {
  user: null,
  playlists: [],
  playing: false,
  item: null,
  //set to null after developing
  token: 'BQDjFs9qrvuL_Bb1dj6SY9aZu6Xx6kIeitL5wh6ylpUr3hZoSpZ8SsGAak1bUiqQeoBDECSuQIBRG531CVLdPVfDEbMsNY7A8B2twKz9PHCSJaRuHtZxMSj2owHeosU4NEWqvtRxQCv8cCbMmHQMqNtnMdrc',
};

const reducer = (state, action) => {
  console.log(action);

  switch(action.type) {
    case "SET_USER":
      return {
        ...state,
        user: action.user
      };

    case "SET_TOKEN":
      return {
        ...state,
        token: action.token
      }

    case "SET_PLAYLISTS":
      return {
        ...state,
        playlists: action.playlists
      }
    default:
      return state;
  }
}

export default reducer;
